import Card from 'react-bootstrap/Card';
import Button from "react-bootstrap/Button";
import {
    deleteOpportunity,
    followInstitution,
    getFollowedInstitutions,
    isFollowingInstitution
} from "../../../services/Api";
import {CardFooter} from "react-bootstrap";
import {EditarOportunidad} from "../institucion/EditarOportunidad";
import {InscripcionEnOportunidad} from "./InscripcionEnOportunidad";
import {useEffect, useState} from "react";

export function CardOportunidadParticipante({oportunidad, actualizarOportunidades}){

    const buttonElement = <Button variant="outline-primary" onClick={() => follow.then()}>Seguir</Button>
    const [institucionesSeguidas, setInstitucionesSeguidas] = useState([]);
    const [followButton, setFollowButton] = useState(buttonElement);

    const checkAndUpdateFollowedInstitutions = async () => {
        const institucionesSeguidas = await getFollowedInstitutions();
        const isFollowing = institucionesSeguidas.includes(oportunidad.institutionEmail);
        setInstitucionesSeguidas(institucionesSeguidas);
        isFollowing ? setFollowButton(<p>Siguiendo</p>) : setFollowButton(buttonElement);
        return institucionesSeguidas;
    }

    const follow = async () => {
        await followInstitution(oportunidad.institutionEmail);
        await checkAndUpdateFollowedInstitutions();
    };

    useEffect(() => {
        checkAndUpdateFollowedInstitutions().then(result => console.log(result));
    }, [])

    const eliminarOportunidad = () => deleteOpportunity(oportunidad.name).then(() => actualizarOportunidades());
    

    return (
        <div className="oportunidad">
            <Card style={{ width: '18rem' }}>
                <Card.Body>
                    <Card.Title>{oportunidad.name}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{`${oportunidad.institutionEmail}`}</Card.Subtitle>
                    <Card.Text>
                        {`${oportunidad.category} en ${oportunidad.city} con modalidad ${oportunidad.modality}. El idioma requerido es ${oportunidad.language} y nivel educativo ${oportunidad.educationalLevel}. Capacidad ${oportunidad.capacity}.`}
                    </Card.Text>
                    <div className="footer-card-oportunidad">
                        {followButton}
                        <InscripcionEnOportunidad actualizarOportunidades={actualizarOportunidades} oportunidadData={oportunidad}/>
                    </div>
                </Card.Body>
            </Card>
        </div>
    );
}
